class Solution {
    public static void main(String[] args) {
        Solution s = new Solution();
        System.out.println(s.findTheDifference("abcd","abcde"));
        System.out.println(s.findTheDifference("","y"));

        System.out.println(s.findTheDifference("",
                ""));


    }
    public char findTheDifference(String s, String t) {

        //validation
        if(s==null || t==null ) return '\0';

        //hash
        int[] freq = new int['z'-'a'+1];

        //count up char in s
        for(int i=0;i<s.length();i++) {
            freq[s.charAt(i)-'a']++;
        }

        //count down every char in t
        for(int i=0;i<t.length();i++) {
            char key = t.charAt(i);
            freq[key-'a']--;
            if(freq[key-'a'] < 0) return key;
        }

        return '\0';

    }
}
